mapper.select:
SELECT COUNT(*) 
  FROM LIKES 
 WHERE IBOARD_NO=#{iboardNo} 
   AND LIKES_MEMBER = #{iboardContent}

	SELECT COUNT(*) 
	  FROM LIKES 
   WHERE IBOARD_NO = #{iboardNo} 
	   AND LIKES_MEMBER = #{likesMember}

mapper.delete:
DELETE 
  FROM LIKES 
 WHERE LIKES_MEMBER = #{likesMember} 
   AND IBOARD_NO = #{iboardNo}
