mapper.update:
UPDATE MEMBER
	SET MEMBER_NAME = #{memberName},
		MEMBER_INFO = #{memberInfo},
MEMBER_PHOTO_EXTEND = #{memberPhotoExtend}
	WHERE MEMBER_ID = #{memberId}
	AND MEMBER_DELETE IS NULL

 UPDATE MEMBER
	SET MEMBER_DELETE = SYSDATE
  WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_DELETE IS NULL
